Describe powershell.body:
# http://inspec.io/docs/reference/resources/powershell/
script = <<-EOH
  ${1:# A PowerShell script}
EOH

describe powershell(script) do
  its('stdout') { should${2:_not} eq '${3:value}' }
  its('stderr') { should${4:_not} eq '${5:value}' }${6}
end

